import { NavHashLink, HashLink } from 'react-router-hash-link';
import { useState, useEffect } from 'react';

import '../../styles/Header.css'

function Header() {

        const [ isTablet, setIsTablet ] = useState(false);
        const [ openBurgerMenu, setOpenBurgerMenu ] = useState(false);

        const handleResize = () => {
            if(window.innerWidth < 1200) {
                setIsTablet(true);
            } else {
                setIsTablet(false)
            }
        };

        useEffect(() => {
            handleResize();
            window.addEventListener('resize', handleResize);

            return () => {
                window.removeEventListener('resize', handleResize);
            }
        }, []);

            const handleBurgerMenu = () => {
                setOpenBurgerMenu(!openBurgerMenu);
            };

    return (
        <header>
            <div className='header-container'>
                <div className='img-container'>
                    <HashLink smooth to='/#section-presentation'><img src='/assets/images/illustration-rÃ©mi.webp' alt="" /></HashLink>

                </div>
                <nav className='navbar'>
                    {!isTablet ? (
                        <ul className='nav-links'>
                            <li> <NavHashLink smooth to='/#about'>A propos</NavHashLink> </li>
                            <li> <NavHashLink smooth to='/#skills'>CompÃ©tences</NavHashLink> </li>
                            <li> <NavHashLink smooth to='/#project'>Projets</NavHashLink> </li>
                            <li> <NavHashLink smooth to='/#contact'>Contact</NavHashLink> </li>
                        </ul>
                    ) : (
                        <div className='burger-menu-container'>
                            <button onClick={ handleBurgerMenu }><img id='burger-menu' src="/assets/icons/burger-bar.png" alt="icone du menu burger" /></button>
                            { openBurgerMenu && (

                            <div className='burger-menu-background'>
                                <ul className='nav-links-mobile'>
                                    <li> <NavHashLink smooth to='/#about'>A propos</NavHashLink> </li>
                                    <li> <NavHashLink smooth to='/#skills'>CompÃ©tences</NavHashLink> </li>
                                    <li> <NavHashLink smooth to='/#project'>Projets</NavHashLink> </li>
                                    <li> <NavHashLink smooth to='/#contact'>Contact</NavHashLink> </li>
                                </ul>
                            </div>

                            )}
                        </div>


                    )  
                    }

                </nav>
            </div>
        </header>
    )
}

export default Header
